// src/components/ResumeUpload.js
import React, { useState } from 'react';
import { useDropzone } from 'react-dropzone';
import { ProgressBar } from 'react-bootstrap';
import './ResumeUpload.css';

const ResumeUpload = () => {
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [progress, setProgress] = useState(0);

  const { getRootProps, getInputProps } = useDropzone({
    accept: '.pdf,.doc,.docx',
    onDrop: acceptedFiles => {
      setLoading(true);
      setFiles(acceptedFiles.map(file => Object.assign(file, {
        preview: URL.createObjectURL(file)
      })));

      // Simulate loading progress
      setProgress(0); // Reset progress to 0
      let progressInterval = setInterval(() => {
        setProgress(prevProgress => {
          if (prevProgress >= 100) {
            clearInterval(progressInterval);
            setLoading(false);
            return 100;
          }
          return prevProgress + 1; // Increase the progress by 1% every 100ms
        });
      }, 100); // Set interval to 100ms
    }
  });

  const getFileIcon = (fileName) => {
    const extension = fileName.split('.').pop();
    switch (extension) {
      case 'pdf':
        return <i className="fas fa-file-pdf"></i>;
      case 'doc':
      case 'docx':
        return <i className="fas fa-file-word"></i>;
      default:
        return <i className="fas fa-file"></i>;
    }
  };

  const thumbs = files.map(file => (
    <div key={file.name} className="thumb">
      <div className="thumb-inner">
        {getFileIcon(file.name)}
        <p>{file.name}</p>
      </div>
    </div>
  ));

  return (
    <section className="container">
      <div {...getRootProps({ className: 'dropzone' })}>
        <input {...getInputProps()} />
        <p>Drag 'n' drop your resume here, or click to select files</p>
      </div>
      {loading && <ProgressBar now={progress} label={`${progress}%`} animated striped variant="info" className="mt-3" />}
      <aside className="thumbs-container">
        {thumbs}
      </aside>
    </section>
  );
}

export default ResumeUpload;
