import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import { Worker, Viewer } from '@react-pdf-viewer/core';
import '@react-pdf-viewer/core/lib/styles/index.css';
import { defaultLayoutPlugin } from '@react-pdf-viewer/default-layout';
import './Dashboard.css';

const Dashboard = () => {
  const location = useLocation();
  const matchPercentage = location.state?.matchPercentage || 0; // Default to 0 if no matchPercentage
  const [pdfData, setPdfData] = useState(null);

  const defaultLayoutPluginInstance = defaultLayoutPlugin();

  useEffect(() => {
    if (location.state?.pdfFile) {
      const pdfBlob = new Blob([location.state.pdfFile], { type: 'application/pdf' });
      setPdfData(URL.createObjectURL(pdfBlob));
    }
  }, [location.state]);

  return (
    <div className="dashboard-container">
      <aside className="sidebar">
        <h2>Score</h2>
        {matchPercentage !== 0 ?  (
        <div className="circular-chart">
          <CircularProgressbar
            value={matchPercentage}
            text={`${matchPercentage}%`}
            styles={buildStyles({
              textColor: '#f88',
              pathColor: '#4caf50',
              trailColor: '#d6d6d6',
            })}
          />
        </div>):(  <p>No comparison result available.</p>)}
      </aside>

      <main className="dashboard">
        <h2>Uploaded PDF</h2>
        {pdfData ? (
          <div className="pdf-viewer">
            <Worker workerUrl={`https://unpkg.com/pdfjs-dist@2.6.347/build/pdf.worker.min.js`}>
              <Viewer
                fileUrl={pdfData}
                plugins={[defaultLayoutPluginInstance]}
              />
            </Worker>
          </div>
        ) : (
          <p>No PDF uploaded.</p>
        )}
      </main>
    </div>
  );
};

export default Dashboard;
