import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { CircularProgressbar, buildStyles } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';
import './Dashboard.css';

const Dashboard = () => {
  const location = useLocation();
  const matchPercentage = location.state?.matchPercentage || 0; // Default to 0 if no matchPercentage
  const missingKeywords = location.state?.missingKeywords || []; // Default to empty array if no missingKeywords

  return (
    <div className="dashboard-container">
      <aside className="sidebar">
        <h2>Score</h2>
        {matchPercentage !== 0 ?  (
        <div className="circular-chart">
          <CircularProgressbar
            value={matchPercentage}
            text={`${matchPercentage}%`}
            styles={buildStyles({
              textColor: '#f88',
              pathColor: '#4caf50',
              trailColor: '#d6d6d6',
            })}
          />
        </div>):(
          <p>No comparison result available.</p>
        )}
      </aside>

      <main className="dashboard">
        <h3>Suggestions for Improvement</h3>
        {missingKeywords.length > 0 ? (
          <div className="suggestions">
            <p>The following keywords are missing from your input:</p>
            <ul>
              {missingKeywords.map((keyword, index) => (
                <li key={index}>{keyword}</li>
              ))}
            </ul>
          </div>
        ) : (
          <p>Great! You have included all the necessary keywords.</p>
        )}
      </main>
    </div>
  );
};

export default Dashboard;
