import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import ResumeUpload from './components/ResumeUpload';
import JobDescriptionInput from './components/JobDescriptionInput';
import Dashboard from './components/Dashboard';
import ProgressBar from 'react-bootstrap/ProgressBar';
import './App.css';
import axios from 'axios';

const Home = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [title, setTitle] = useState('Loading...');
  const [jobDescription, setJobDescription] = useState('');
  const [resumeFile, setResumeFile] = useState(null);
  const [matchPercentage, setMatchPercentage] = useState(null);

  const handleScan = async () => {
    if (!resumeFile || !jobDescription) {
      alert("Please upload a resume and enter a job description.");
      return;
    }

    setLoading(true);

    const formData = new FormData();
    formData.append('pdfFile', resumeFile);
    formData.append('textInput', jobDescription);

    try {
      const response = await axios.post('http://localhost:4000/compare', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setMatchPercentage(response.data.matchPercentage);
      navigate('/dashboard');
    } catch (error) {
      console.error("Error comparing text with resume:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <h1>Scan Macha</h1>
      <div className="main-container">
        {loading ? (
          <div className="progress-section">
            <h2>Analyzing...</h2>
            <ProgressBar now={progress} animated striped variant="info" className="mt-3" />
          </div>
        ) : (
          <>
            <ResumeUpload setLoading={setLoading} setProgress={setProgress} setTitle={setTitle} setResumeFile={setResumeFile} />
            <JobDescriptionInput setJobDescription={setJobDescription} />
          </>
        )}
      </div>
      {!loading && <button className="scan-button" onClick={handleScan}>Scan</button>}
    </div>
  );
};

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/dashboard" element={<Dashboard />} />
        <Route path="/" element={<Home />} />
      </Routes>
    </Router>
  );
}

export default App;
