// src/components/ScanScreen.js
import React, { useState, useEffect } from 'react';
import { useDropzone } from 'react-dropzone';
import { useNavigate } from 'react-router-dom';
import { ProgressBar } from 'react-bootstrap';
import './ScanScreen.css';

const ScanScreen = () => {
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [title, setTitle] = useState('Loading...');
  const [jobDescription, setJobDescription] = useState('');
  const navigate = useNavigate();

  const { getRootProps, getInputProps } = useDropzone({
    accept: '.pdf,.doc,.docx',
    onDrop: acceptedFiles => {
      setLoading(true);
      setFiles(acceptedFiles.map(file => Object.assign(file, {
        preview: URL.createObjectURL(file)
      })));

      // Simulate loading progress
      setProgress(0); // Reset progress to 0
      let progressInterval = setInterval(() => {
        setProgress(prevProgress => {
          if (prevProgress >= 100) {
            clearInterval(progressInterval);
            setLoading(false);
            setTitle('Loading...');
            return 100;
          }
          return prevProgress + 2; // Increase the progress by 2% every 50ms
        });
      }, 50); // Set interval to 50ms

      setTimeout(() => {
        setTitle('Analyzing...');
      }, 3000); // Change title after 3 seconds
    }
  });

  const getFileIcon = (fileName) => {
    const extension = fileName.split('.').pop();
    switch (extension) {
      case 'pdf':
        return <i className="fas fa-file-pdf"></i>;
      case 'doc':
      case 'docx':
        return <i className="fas fa-file-word"></i>;
      default:
        return <i className="fas fa-file"></i>;
    }
  };

  const thumbs = files.map(file => (
    <div key={file.name} className="thumb">
      <div className="thumb-inner">
        {getFileIcon(file.name)}
        <p>{file.name}</p>
      </div>
    </div>
  ));

  const handleJobDescriptionChange = (e) => {
    setJobDescription(e.target.value);
  };

  const handleScan = () => {
    setLoading(true);
    setTimeout(() => {
      navigate('/dashboard');
    }, 5000); 
  };

  return (
    <div className="scan-screen">
      <div className="input-container">
        <section className="container dropzone-container">
          <div {...getRootProps({ className: 'dropzone' })}>
            <input {...getInputProps()} />
            <p>Drag 'n' drop your resume here, or click to select files</p>
          </div>
          <aside className="thumbs-container">
            {thumbs}
          </aside>
        </section>
        <textarea
          value={jobDescription}
          onChange={handleJobDescriptionChange}
          placeholder="Enter job description here"
          rows="10"
          cols="50"
          className="job-description-input"
        />
      </div>
      {loading && (
        <div className="progress-section">
          <h2>{title}</h2>
          <ProgressBar now={progress} label={`${progress}%`} animated striped variant="info" className="mt-3" />
        </div>
      )}
      {!loading && <button className="scan-button" onClick={handleScan}>Scan</button>}
    </div>
  );
}

export default ScanScreen;
