import React, { useState, useEffect } from 'react';
import { useDropzone } from 'react-dropzone';
import { ProgressBar } from 'react-bootstrap';
import axios from 'axios';
import './ResumeUpload.css';

const ResumeUpload = ({ setMatchPercentage }) => {
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const [title, setTitle] = useState('Loading...');

  const { getRootProps, getInputProps } = useDropzone({
    accept: '.pdf,.doc,.docx',
    onDrop: acceptedFiles => {
      setLoading(true);
      setFiles(acceptedFiles.map(file => Object.assign(file, {
        preview: URL.createObjectURL(file)
      })));

      // Simulate loading progress
      setProgress(0);
      let progressInterval = setInterval(() => {
        setProgress(prevProgress => {
          if (prevProgress >= 100) {
            clearInterval(progressInterval);
            setLoading(false);
            setTitle('Loading...');
            return 100;
          }
          return prevProgress + 2;
        });
      }, 50);

      setTimeout(() => {
        setTitle('Analyzing...');
      }, 3000);

      handleFileUpload(acceptedFiles[0]);
    }
  });

  const handleFileUpload = async (file) => {
    const formData = new FormData();
    formData.append('pdfFile', file);

    try {
      const jobDescription = localStorage.getItem('jobDescription') || '';
      formData.append('textInput', jobDescription);

      const response = await axios.post('http://localhost:3000/compare', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setMatchPercentage(response.data.matchPercentage);
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  const getFileIcon = (fileName) => {
    const extension = fileName.split('.').pop();
    switch (extension) {
      case 'pdf':
        return <i className="fas fa-file-pdf"></i>;
      case 'doc':
      case 'docx':
        return <i className="fas fa-file-word"></i>;
      default:
        return <i className="fas fa-file"></i>;
    }
  };

  const thumbs = files.map(file => (
    <div key={file.name} className="thumb">
      <div className="thumb-inner">
        {getFileIcon(file.name)}
        <p>{file.name}</p>
      </div>
    </div>
  ));

  return (
    <section className="container">
      <div {...getRootProps({ className: 'dropzone' })}>
        <input {...getInputProps()} />
        <p>Drag 'n' drop your resume here, or click to select files</p>
      </div>
      {loading && <div className="progress-section">
        <h2>{title}</h2>
        <ProgressBar now={progress} label={`${progress}%`} animated striped variant="info" className="mt-3" />
      </div>}
      <aside className="thumbs-container">
        {thumbs}
      </aside>
    </section>
  );
}

export default ResumeUpload;
